package homework.zhuyoufeng.test.flowcontrol;/** * 编写一个程序，用命令行接受学生的考试成绩， * 参考人数不一定，至少1位，最多5位。 * 计算参考同学成绩的总分、平均分。 * @author zhuyoufeng * */public class StudentGoal{		public static void main(String[] args) {		double sum = 0;		double avg = 0;		for (int i=0; i<args.length; i++) {			double g = Double.parseDouble(args[i]);			sum += g;		}		avg = sum / args.length;		System.out.println("总分为：" + sum);		System.out.println("平均分为：" + avg);	}}